var spoken = context.get('spoken') || false;
var threshold = 60;
var outString = "yes";
var outMsg = {payload: "", text : outString};

if (!spoken) {
    if (msg.payload >= threshold) {
        outMsg.payload = outString;
        spoken = true;
    }
}

if (msg.payload < threshold) {
    spoken = false;
    outMsg.text = "";
}

console.log(outMsg);

context.set('spoken', spoken);
return outMsg;
